import React from "react";
import StripeCheckout from "react-stripe-checkout";
import axios from "axios";

const StripeButton = ({ price }) => {
  const stripePrice = price * 100;
  const publicKey =
    "pk_test_51HHbIrKx1GFK0jKDF4ih2zqyJobVb2RpyYXmf53LAlayVqT00qxZfgLsMw26Mb5Vyi5eV2hB0zcEQ69Xp5GoNhav00VygjfmsD";

  const onToken = (token) => {
    axios({
      url: "payment",
      method: "post",
      data: {
        amount: stripePrice,
        token
      },
    })
      .then((response) => {
        alert("Payment Successful");
      })
      .catch((error) => {
        console.log("payment error: ", error);
        alert("Unsuccessful Payment");
      });
  };

  return (
    <StripeCheckout
      stripeKey={publicKey}
      name="Street Collectiv"
      shippingAddress={true}
      billingAddress={false}
      allowRememberMe={false}
      amount={stripePrice}
      token={onToken}
    >
      <button className="custom-button">Checkout</button>
    </StripeCheckout>
  );
};

export default StripeButton;
